const fs = require('fs')
const path = require('path')
const { execSync } = require('child_process')
const chalk = require('chalk')

class BuildTool {
  constructor(options = {}) {
    this.options = {
      srcDir: 'src',
      distDir: 'dist',
      mode: 'production',
      ...options
    }
  }

  // æ¸…ç†è¾“å‡ºç›®å½•
  clean() {
    console.log(chalk.blue('ğŸ§¹ æ¸…ç†è¾“å‡ºç›®å½•...'))
    if (fs.existsSync(this.options.distDir)) {
      fs.rmSync(this.options.distDir, { recursive: true })
    }
    fs.mkdirSync(this.options.distDir, { recursive: true })
  }

  // æ„å»ºCSS
  buildCSS() {
    console.log(chalk.green('ğŸ¨ æ„å»ºCSS...'))
    try {
      execSync(`sass ${this.options.srcDir}/scss:${this.options.distDir}/css --style=compressed`, {
        stdio: 'inherit'
      })
    } catch (error) {
      console.error(chalk.red('CSSæ„å»ºå¤±è´¥:'), error.message)
      process.exit(1)
    }
  }

  // æ„å»ºJavaScript
  buildJS() {
    console.log(chalk.yellow('ğŸ“¦ æ„å»ºJavaScript...'))
    try {
      execSync(`webpack --mode=${this.options.mode}`, {
        stdio: 'inherit'
      })
    } catch (error) {
      console.error(chalk.red('JavaScriptæ„å»ºå¤±è´¥:'), error.message)
      process.exit(1)
    }
  }

  // å¤åˆ¶é™æ€èµ„æº
  copyAssets() {
    console.log(chalk.cyan('ğŸ“ å¤åˆ¶é™æ€èµ„æº...'))
    const assetsDir = path.join(this.options.srcDir, 'assets')
    const targetDir = path.join(this.options.distDir, 'assets')

    if (fs.existsSync(assetsDir)) {
      this.copyDirectory(assetsDir, targetDir)
    }
  }

  // é€’å½’å¤åˆ¶ç›®å½•
  copyDirectory(src, dest) {
    if (!fs.existsSync(dest)) {
      fs.mkdirSync(dest, { recursive: true })
    }

    const files = fs.readdirSync(src)

    files.forEach(file => {
      const srcPath = path.join(src, file)
      const destPath = path.join(dest, file)

      if (fs.statSync(srcPath).isDirectory()) {
        this.copyDirectory(srcPath, destPath)
      } else {
        fs.copyFileSync(srcPath, destPath)
      }
    })
  }

  // ç”Ÿæˆæ„å»ºæŠ¥å‘Š
  generateReport() {
    console.log(chalk.magenta('ğŸ“Š ç”Ÿæˆæ„å»ºæŠ¥å‘Š...'))
    const report = {
      buildTime: new Date().toISOString(),
      mode: this.options.mode,
      files: this.getFileList(this.options.distDir)
    }

    fs.writeFileSync(
      path.join(this.options.distDir, 'build-report.json'),
      JSON.stringify(report, null, 2)
    )
  }

  // è·å–æ–‡ä»¶åˆ—è¡¨
  getFileList(dir) {
    const files = []
    const items = fs.readdirSync(dir)

    items.forEach(item => {
      const fullPath = path.join(dir, item)
      const stat = fs.statSync(fullPath)

      if (stat.isDirectory()) {
        files.push(...this.getFileList(fullPath))
      } else {
        files.push({
          path: path.relative(this.options.distDir, fullPath),
          size: stat.size,
          modified: stat.mtime
        })
      }
    })

    return files
  }

  // æ‰§è¡Œå®Œæ•´æ„å»º
  async build() {
    const startTime = Date.now()

    console.log(chalk.bold.blue('ğŸš€ å¼€å§‹æ„å»º...'))

    try {
      this.clean()
      this.buildCSS()
      this.buildJS()
      this.copyAssets()
      this.generateReport()

      const duration = Date.now() - startTime
      console.log(chalk.bold.green(`âœ… æ„å»ºå®Œæˆ! è€—æ—¶: ${duration}ms`))
    } catch (error) {
      console.error(chalk.bold.red('âŒ æ„å»ºå¤±è´¥:'), error.message)
      process.exit(1)
    }
  }
}

