        name: 'withProps',
        message: 'æ˜¯å¦éœ€è¦Propsæ¥å£?',
        default: true
      }
    ])

    const componentDir = path.join(this.srcDir, 'components', answers.name)

    // åˆ›å»ºç»„ä»¶ç›®å½•
    if (!fs.existsSync(componentDir)) {
      fs.mkdirSync(componentDir, { recursive: true })
    }

    // ç”Ÿæˆç»„ä»¶æ–‡ä»¶
    const componentTemplate = this.getTemplate('component', answers.type)
    const componentContent = this.renderTemplate(componentTemplate, answers)

    fs.writeFileSync(
      path.join(componentDir, `${answers.name}.tsx`),
      componentContent
    )

    // ç”Ÿæˆæ ·å¼æ–‡ä»¶
    const styleContent = `.${answers.name.toLowerCase()} {\n  /* ç»„ä»¶æ ·å¼ */\n}`
    fs.writeFileSync(
      path.join(componentDir, `${answers.name}.module.scss`),
      styleContent
    )

    // ç”Ÿæˆindexæ–‡ä»¶
    const indexContent = `export { default } from './${answers.name}'`
    fs.writeFileSync(
      path.join(componentDir, 'index.ts'),
      indexContent
    )

    console.log(chalk.green(`âœ… ç»„ä»¶ ${answers.name} åˆ›å»ºæˆåŠŸ!`))
  }

  // ç”Ÿæˆè‡ªå®šä¹‰Hook
  async generateHook() {
    const answers = await inquirer.prompt([
      {
        type: 'input',
        name: 'name',
        message: 'Hookåç§° (ä¸å«useå‰ç¼€):'
      },
      {
        type: 'list',
        name: 'category',
        message: 'Hookåˆ†ç±»:',
        choices: ['state', 'effect', 'context', 'utility']
      }
    ])

    const hookName = `use${answers.name}`
    const hooksDir = path.join(this.srcDir, 'hooks')

    if (!fs.existsSync(hooksDir)) {
      fs.mkdirSync(hooksDir, { recursive: true })
    }

    const hookTemplate = this.getTemplate('hook', answers.category)
    const hookContent = this.renderTemplate(hookTemplate, {
      ...answers,
      hookName
    })

    fs.writeFileSync(
      path.join(hooksDir, `${hookName}.ts`),
      hookContent
    )

    console.log(chalk.green(`âœ… Hook ${hookName} åˆ›å»ºæˆåŠŸ!`))
  }

  // ç”ŸæˆAPIæœåŠ¡
  async generateService() {
    const answers = await inquirer.prompt([
      {
        type: 'input',
        name: 'name',
        message: 'æœåŠ¡åç§°:'
      },
      {
        type: 'input',
        name: 'baseUrl',
        message: 'APIåŸºç¡€URL:',
        default: '/api'
      }
    ])

    const servicesDir = path.join(this.srcDir, 'services')

    if (!fs.existsSync(servicesDir)) {
      fs.mkdirSync(servicesDir, { recursive: true })
    }

    const serviceTemplate = this.getTemplate('service')
    const serviceContent = this.renderTemplate(serviceTemplate, answers)

    fs.writeFileSync(
      path.join(servicesDir, `${answers.name}Service.ts`),
