  // è·å–æ¨¡æ¿
  getTemplate(type, subType = '') {
    const templatePath = path.join(
      this.templatesDir,
      `${type}${subType ? `-${subType}` : ''}.template`
    )

    if (fs.existsSync(templatePath)) {
      return fs.readFileSync(templatePath, 'utf8')
